package org.pathwayeditor.businessobjects.hibernate.pojos;

import java.io.Serializable;



/**
 * TextProperty generated by hbm2java
 */
public class HibTextProperty extends HibProperty implements Serializable {
	private static final long serialVersionUID = 2408749022194227955L;
	
	private String textValue;
	private final PropertyVisitor visitor = PropertyVisitor.getInstance();

    public HibTextProperty() {
    }

    public HibTextProperty(HibCanvas hibCanvas, int creationSerial, String textValue) {
        super(hibCanvas, creationSerial);        
       this.textValue = textValue;
    }
   
    
    public HibTextProperty(HibCanvas newCanvas, HibTextProperty other){
    	super(newCanvas, other);
    	this.textValue = other.textValue;
    }
    
    public String getTextValue() {
        return this.textValue;
    }
    
    public void setTextValue(String textValue) {
        this.textValue = textValue;
    }

    @Override
    public HibTextProperty copy(HibCanvas newCanvas){
    	return visitor.copyTextProperty(newCanvas, this);
    }
}


